import React, { useState,useEffect } from 'react';
import "../App.css";
import logo from "../logo.png"

import products from "../products.json";
function Products()
{

    const [items, setItems] = useState([]);

    const [inputData, setInputData] = useState({});


    const [inputId, setInputId] = useState('');
    const [inputText, setInputText] = useState('');
    


    



  const handleInputChange1 = (event) => {
    inputData.id=event.target.value
    setInputData(inputData);
  };


    const widthStyle = {width: '50px'}
    const paddingStyle = {padding: '20px'}

  useEffect(() => {
    setItems(products);
  }, []);

  const deleteItem = (id) => {
    const newItems = items.filter((item) => item.id !== id);
    setItems(newItems);
  };

  const redirectToDashboard = () => {
    window.location.href = '/dashboard';
  };


  const editItem = (itemId) => {

    const index = items.findIndex(item => item.id === itemId);

    setInputData(items[index])



    //items[index].name

    
  };


  /*
  const handleInputChange1 = (event) => {
    //inputData.id=event.target.value;
  };
  */





  const updateItem = (itemId, newName, newPrice) => {



    const index = items.findIndex(item => item.id === itemId);

    const updatedData = [ ...items ];
    

    if (index !== -1) {
      updatedData[index].name = newName;
      updatedData[index].price = newPrice;
      setItems(updatedData);
    }
  };




    return(
        
        <div>
             
 <div class="header_design">

<img src={logo} class="he1"/>
<font class="he2" color="black" onClick={redirectToDashboard}>Dashboard</font>
<font class="he3" color="blue">Products</font>
<font class="he4" color="black">Orders</font>










</div>

<div className="products_div3">
            
<h1>Products </h1>

<table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Price</th>
            <th>Stock Quantity</th>
            <th>Function</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.price}</td>
              <td>{item.stock}</td>
              <td style={paddingStyle}><span onClick={() => editItem(item.id)} className='upd'>Edit Item</span>   &nbsp;&nbsp;   <span onClick={() => deleteItem(item.id)} className='table_div'>Delete Item</span></td>
              
            </tr>
          ))}
        </tbody>
      </table>
      <br/>
      <h2>Add / Update</h2>
      <input type="text" placeholder="ID" className="et1" style={widthStyle} value={inputData.id} onChange={handleInputChange1}/>
      &nbsp;&nbsp;&nbsp;
      <input type="text" placeholder="Name" className="et1" value={inputData.name}/>
      &nbsp;&nbsp;&nbsp;
      <input type="text" placeholder="Category" className="et1" value={inputData.category}/>
      &nbsp;&nbsp;&nbsp;
      <input type="text" placeholder="Price" className="et1" value={inputData.price}/>
      &nbsp;&nbsp;&nbsp;
      <input type="text" placeholder="Stock Quantity" className="et1" value={inputData.stock}/>



      <br></br>
      <br></br>

      <div onClick={() => updateItem()} className="upd">Add / Update</div>

</div>

        </div>
    )
}

export default Products;




