import "../App.css";
import logo from "../logo.png"
import React, { useState,useEffect } from 'react';
import orders from "../orders.json";
function Orders()
{


    const widthStyle = {width: '50px'}
    const paddingStyle = {padding: '20px'}
    const trStyle = {backgroundColor: 'bisque'}


    const redirectToDashboard = () => {
        window.location.href = '/dashboard';
      };
      const redirectToProducts = () => {
        window.location.href = '/products';
      };


      const [items, setItems] = useState([]);
      useEffect(() => {
        setItems(orders);
      }, []);

      










    return(
<div>
<div className="header_design">

<img src={logo} className="he1"/>
<font className="he2" color="black" onClick={redirectToDashboard}>Dashboard</font>
<font className="he3" color="black" onClick={redirectToProducts}>Products</font>
<font className="he4" color="blue">Orders</font>










</div>


<div className="products_div3">
            
<h1>Products </h1>

<table>
        <thead>
          <tr>
            <th style={trStyle}>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Price</th>
            <th>Stock Quantity</th>
            <th>Function</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.price}</td>
              <td>{item.stock}</td>
              <td style={paddingStyle}><span onClick={() => editItem(item.id)} className='upd'>Edit Item</span>   &nbsp;&nbsp;   <span onClick={() => deleteItem(item.id)} className='table_div'>Delete Item</span></td>
              
            </tr>
          ))}
        </tbody>

        </table>
        </div>

</div>
    );
}

export default Orders
