import logo from "./logo.png"
import React, { useState,useEffect } from 'react';

import { Routes,Route} from "react-router-dom";
import Products from "./pages/Products";
import "./App.css";
import products_img from "./products_img.png";

import products from "./products.json";



var a="3";
const App = () => 
{


  const [items, setItems] = useState([]);

  useEffect(() => {
    setItems(products);
  }, []);

  const deleteItem = (id) => {
    const newItems = items.filter((item) => item.id !== id);
    setItems(newItems);
  };




  const redirectToSpecificPath = () => {
    window.location.href = '/products';
  };








  return(
<div>
  <div class="header_design">

    <img src={logo} class="he1"/>
    <font class="he2" color="blue">Dashboard</font>
    <font class="he3" color="black">Products</font>
    <font class="he4" color="black">Orders</font>










  </div>

  <div class="products_div">
    <h2>
      Products
    </h2>



    <p class="products_p"> Household essentials encompass a variety of products that are essential for maintaining a clean, organized, and functional home. These items can be conveniently ordered online and delivered to your doorstep through grocery delivery services. Here are some common household items available for delivery</p>
    <img src={products_img} class="products_im"/>

    <div class="products_div2" onclick={redirectToSpecificPath}>
View Products 
    </div>
    <b class="products_span">Total Stocks Available : 10</b>






  </div>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>

  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>

  <div>
      {items.map((item) => (
        <div key={item.id}>
          <h3>{item.name}</h3>
          <button onClick={() => deleteItem(item.id)}>Delete</button>
        </div>
      ))}
    </div>

  <Routes>
    <Route path='/products' element={<Products/>} />
  </Routes>



</div>
  );
}

export default App;
