import React, { useState,useEffect } from 'react';
import "../App.css";
import logo from "../logo.png"

import products from "../products.json";
function Products()
{

    const [items, setItems] = useState([]);

  useEffect(() => {
    setItems(products);
  }, []);

  const deleteItem = (id) => {
    const newItems = items.filter((item) => item.id !== id);
    setItems(newItems);
  };

    return(
        
        <div>
             
 <div class="header_design">

<img src={logo} class="he1"/>
<font class="he2" color="black">Dashboard</font>
<font class="he3" color="blue">Products</font>
<font class="he4" color="black">Orders</font>










</div>

<div className="products_div3">
            
<h1>Products </h1>

<table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Price</th>
            <th>Stock Quantity</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.price}</td>
              <td>{item.stock}</td>
              <td><button onClick={() => deleteItem(item.id)} style="background-color:red;">Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
</div>

        </div>
    )
}

export default Products;




