import "../App.css";
import logo from "../logo.png"
import React, { useState,useEffect } from 'react';
import orders from "../orders.json";
function Orders()
{


    const widthStyle = {width: '50px'}
    const paddingStyle = {padding: '20px'}
    const trStyle = {backgroundColor: 'bisque'}
    const orangeStyle = {backgroundColor: 'chocolate'}


    const redirectToDashboard = () => {
        window.location.href = '/dashboard';
      };
      const redirectToProducts = () => {
        window.location.href = '/products';
      };


      const [items, setItems] = useState([]);
      useEffect(() => {
        setItems(orders);
      }, []);




      //functions

      const deleteItem = (id) => {
        const newItems = items.filter((item) => item.id !== id);
        setItems(newItems);
      };

      










    return(
<div>
<div className="header_design">

<img src={logo} className="he1"/>
<font className="he2" color="black" onClick={redirectToDashboard}>Dashboard</font>
<font className="he3" color="black" onClick={redirectToProducts}>Products</font>
<font className="he4" color="blue">Orders</font>










</div>


<div className="products_div3">
            
<h1>Orders </h1>

<table>
        <thead>
          <tr>
            <th style={trStyle}>Order ID</th>
            <th>Customer Name</th>
            <th>Order Date</th>
            <th>Status</th>
            <th>Function</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.date}</td>
              <td><select className="select_view"><option value="Ordered">Ordered</option><option value="Shipped">Shipped</option><option value="Dispached">Dispached</option><option value="Delivered">Delivered</option></select></td>
              <td style={paddingStyle}><span className='upd'>View Order</span>   &nbsp;&nbsp;   <span onClick={() => deleteItem(item.id)} className='table_div' style={orangeStyle}>Delete Order</span></td>
              
            </tr>
          ))}
        </tbody>

        </table>

        <div>
            <b>Order Details</b>
        </div>
        </div>

</div>
    );
}

export default Orders
